import React from 'react'
import Headerplus from "./components/Headerplus";
import Down from "./components/Down";
import { auth } from "./firebase";

function Forget() {


  const [reset, setReset] = use("");

  const resetP = (e) => {
  e.preventDefault();
  auth
    .sendPasswordResetEmail(reset)
    .then((auth) => {
      history.push("/");
    })
    .catch((error) => alert(error.message));
  
};

    return (
        <div>
        <Headerplus />
            <h5>enter your email</h5>
            <input type='text' value={reset} onChange={(e) => setReset(e.target.value)} ></input>
            <button onClick={resetP} type='submit'>submit</button>
            <Down />
        </div>
    )
}

export default Forget
