import React, { useState } from "react";
import Headerplus from "./components/Headerplus";
import Down from "./components/Down";
import LinearBuffer from "./components/Down";
import FitnessCenterIcon from "@material-ui/icons/FitnessCenter";
import "./login.css";
import { Link, useHistory } from "react-router-dom";
import { auth } from "./firebase";
import Button from '@material-ui/core/Button';

function Join() {
  const history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const signIn = (e) => {
    e.preventDefault();
    auth
      .signInWithEmailAndPassword(email, password)
      .then((auth) => {
        history.push("/");
      })
      .catch((error) => alert(error.message));
    // firebase login
  };

  const register = (e) => {
    e.preventDefault();

    auth
      .createUserWithEmailAndPassword(email, password)
      .then((auth) => {
        if (auth) {
          history.push("/");
        }

        console.log(auth);
      })

      .catch((error) => alert(error.message));
  };

  return (
    // <div>
    //   <Headerplus />
    //   <div className="login__container">
    //     <Link to="/">
    //       <FitnessCenterIcon />
    //     </Link>
    //     <form>
    //       <h5>Email</h5>
    //       <input
    //         type="email"
    //         value={email}
    //         onChange={(e) => setEmail(e.target.value)}
    //       ></input>
    //       <h5>Password</h5>
    //       <input
    //         type="password"
    //         value={password}
    //         onChange={(e) => setPassword(e.target.value)}
    //       ></input>
    //       <button class="btn btn-dark" type="submit" onClick={signIn}>
    //         Sign In
    //       </button>
    //       <Link to="/forget">
    //         <button class="btn btn-dark">Forget Password</button>
    //       </Link>
    //       <p>
    //         Now you can browse privately, and other people who use this device
    //         wonâ€™t see your activity. However, downloads, bookmarks and reading
    //         list items will be saved.
    //       </p>
    //       <button class="btn btn-dark" onClick={register}>
    //         Create Account
    //       </button>
    //     </form>
    //   </div>
    //   <Down />
    // </div>

    <div>
      <Headerplus />
      <form className="signin__container">
        <div class="form-group">
          <label for="exampleInputEmail1">Email address</label>
          <input
          placeholder="example@gmail.com"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            class="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
          />
          <small id="emailHelp" class="form-text text-muted">
            We'll never share your email with anyone else.
          </small>
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            class="form-control"
            id="exampleInputPassword1"
          />
          <div>
            <button
              class="btn btn-primary signin__button"
              type="submit"
              onClick={signIn}
            >
              Sign In
            </button>
          </div>
          {/* <div class="form-group form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1" />
    <label class="form-check-label" for="exampleCheck1">Check me out</label>
  </div> */}
          <button
            class="btn btn-dark create__button"
            type="submit"
            onClick={register}
          >
            Create Account
          </button>
        </div>
        <Link to="/forget">
          <button class="btn btn-dark forgot_button">Forgot Password</button>
        </Link>
      </form>

      <Down />
    </div>
  );
}

export default Join;
