import React, { useState } from "react";
import Headerplus from "./components/Headerplus";
import Down from "./components/Down";
import { auth } from "./firebase";
import "./login.css";


function Forget() {
  const [reset, setReset] = useState("");

  const resetP = (e) => {
    e.preventDefault();
    auth
      .sendPasswordResetEmail(reset)
      .then((auth) => {
        //   history.push("/");
        window.alert("reset email send on your mail id");
      })
      .catch((error) => alert(error.message));
  };

  return (<div>
      <Headerplus />
    <div className="forget__container">
      <h5>enter your email</h5>
      <input
        class="form-control"
        id="exampleInputEmail1"
        aria-describedby="emailHelp"
        type="text"
        value={reset}
        onChange={(e) => setReset(e.target.value)}
      ></input>
      <button class="btn btn-dark" onClick={resetP} type="submit">
        submit
      </button>
    </div>
      <Down />
      </div>
  );
}

export default Forget;
