import React, { useState } from "react";
import Headerplus from "./components/Headerplus";
import Down from "./components/Down";
import FitnessCenterIcon from "@material-ui/icons/FitnessCenter";
import "./login.css";
import { Link, useHistory } from "react-router-dom";
import { auth } from "./firebase";

function Join() {
  const history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const signIn = (e) => {
    e.preventDefault();
    auth
      .signInWithEmailAndPassword(email, password)
      .then((auth) => {
        history.push("/");
      })
      .catch((error) => alert(error.message));
    // firebase login
  };

  const register = (e) => {
    e.preventDefault();

    auth
      .createUserWithEmailAndPassword(email, password)
      .then((auth) => {
        if (auth) {
          history.push("/");
        }

        console.log(auth);
      })

      .catch((error) => alert(error.message));
  };

  return (
    <div>
      <Headerplus />
      <div className="login__container">
        <Link to="/">
          <FitnessCenterIcon />
        </Link>
        <form>
          <h5>Email</h5>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          ></input>
          <h5>Password</h5>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          ></input>
          <button class="btn btn-dark" type="submit" onClick={signIn}>
            Sign In
          </button>
          <button class="btn btn-dark">Forget Password
          </button>
          <p>
            Now you can browse privately, and other people who use this device
            wonâ€™t see your activity. However, downloads, bookmarks and reading
            list items will be saved.
          </p>
          <Link to='/forget'>
          <button class="btn btn-dark" onClick={register}>Create Account</button></Link>
        </form>
      </div>
      <Down />
    </div>
  );
}

export default Join;
