import React, { useState } from "react";
import styled from "styled-components";
import FitnessCenterIcon from "@material-ui/icons/FitnessCenter";
import AccountBoxIcon from "@material-ui/icons/AccountBox";
import { Link } from "react-router-dom";
// import { useState } from "../Join";
import { auth } from "../firebase";
import { useHistory } from "react-router-dom";

// import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

function Headerplus() {
  // const [email, setEmail] = useState("");

  // const [email, setEmail] = useState('');

  // const handleAuthentication = () => {
  //   if (user) {
  //     auth.signOut();
  //   }
  // };
  const history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const signIn = (e) => {
    e.preventDefault();
    auth
      .signInWithEmailAndPassword(email, password)
      .then((auth) => {
        history.push("/");
        window.alert("Successfully Logged In!");
      })
      .catch((error) => alert(error.message));
    // firebase login
  };

  const register = (e) => {
    e.preventDefault();

    auth
      .createUserWithEmailAndPassword(email, password)
      .then((auth) => {
        if (auth) {
          history.push("/");
          window.alert("Account Created Successfully!");
        }

        console.log(auth);
      })

      .catch((error) => alert(error.message));
  };
  // forgot password functionality

  const [reset, setReset] = useState("");

  const resetP = (e) => {
    e.preventDefault();
    auth
      .sendPasswordResetEmail(reset)
      .then((auth) => {
        //   history.push("/");
        window.alert("Your password reset email has been sent");
      })
      .catch((error) => alert(error.message));
  };

  return (
    <div>
      {/* <div class="alert alert-dark alert-dismissible fade show" role="alert">
        <strong>Holy guacamole!</strong> You should check in on some of those
        fields below.
        <button
          type="button"
          class="close"
          data-dismiss="alert"
          aria-label="Close"
        >
          <span aria-hidden="true">&times;</span>
        </button>
      </div> */}

      <NavWrapper>
        <nav class="navbar navbar-expand-lg navbar-light bg-light">
          <FitnessCenterIcon />
          <h1 class="logo-text">Real Steel Fitness Club</h1>
          <button
            class="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav mr-auto">
              <li class="nav-item active">
                <Link class="nav-link" to="/">
                  Home <span class="sr-only">(current)</span>
                </Link>
              </li>

              <li class="nav-item">
                <Link class="nav-link" to="/workoutplans">
                  Workout Plans
                </Link>
              </li>
              <li class="nav-item">
                <Link class="nav-link" to="/reviews">
                  Reviews
                </Link>
              </li>
              <li class="nav-item">
                <Link class="nav-link" to="/trials">
                  Trials
                </Link>
              </li>
            </ul>
            <ul class="navbar-nav">
              <li class="nav-item">
                <Link class="nav-link" to="/contactus">
                  Contact Us <span class="sr-only">(current)</span>
                </Link>
              </li>
              {/* <li class="nav-item">
                <div onClick={handleAuthentication}></div>
                <span>Hello {!user ? "Guest" : user.email}</span>
                <span>{user ? "Sign Out" : "Sign In"}</span>
                {/* <Link class="nav-link" to="/contactus">
                  Hello Guest <span class="sr-only">(current)</span>
                </Link> */}
              {/* </li> */}
              <li class="nav-item">
                <Link type="button"  data-toggle="modal"
                    data-target="#exampleModal" class="btn-login btn" to="/join">
                  {/* <button
                    type="button"
                    class="btn btn-primary"
                    data-toggle="modal"
                    data-target="#exampleModal"
                  >
                    Launch demo modal
                  </button> */}
                  <div
                    class="modal fade"
                    id="exampleModal"
                    tabindex="-1"
                    aria-labelledby="exampleModalLabel"
                    aria-hidden="true"
                  >
                    <div class="modal-dialog">
                      <div class="modal-content">
                        <div class="modal-header">
                          <h5 class="modal-title" id="exampleModalLabel">
                            Modal title
                          </h5>
                          <button
                            type="button"
                            class="close"
                            data-dismiss="modal"
                            aria-label="Close"
                          >
                            <span aria-hidden="true">&times;</span>
                          </button>
                        </div>
                        <div class="modal-body">
                          <form className="signin__container">
                            <div class="form-group">
                              <label for="exampleInputEmail1">
                                Email address
                              </label>
                              <input
                                placeholder="example@gmail.com"
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                class="form-control"
                                id="exampleInputEmail1"
                                aria-describedby="emailHelp"
                              />
                              <small
                                id="emailHelp"
                                class="form-text text-muted"
                              >
                                We'll never share your email with anyone else.
                              </small>
                            </div>
                            <div class="form-group">
                              <label for="exampleInputPassword1">
                                Password
                              </label>
                              <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                class="form-control"
                                id="exampleInputPassword1"
                              />
                              <div>
                                <button
                                  class="btn btn-primary signin__button"
                                  type="submit"
                                  onClick={signIn}
                                >
                                  Sign In
                                </button>
                              </div>
                              {/* <div class="form-group form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1" />
    <label class="form-check-label" for="exampleCheck1">Check me out</label>
  </div> */}
                              <button
                                class="btn btn-dark create__button"
                                type="submit"
                                onClick={register}
                              >
                                Create Account
                              </button>
                            </div>
                            <Link to="/forgot">
                              <button class="btn btn-dark forgot_button">
                                Forgot Password
                              </button>
                            </Link>
                          </form>
                        </div>
                        <div class="modal-footer">
                          <button
                            type="button"
                            class="btn btn-secondary"
                            data-dismiss="modal"
                          >
                            Close
                          </button>
                          <button type="button" class="btn btn-primary">
                            Save changes
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                  <AccountBoxIcon /> Join Now
                </Link>
              </li>
            </ul>
          </div>
        </nav>
      </NavWrapper>
    </div>
  );
}

export default Headerplus;

const NavWrapper = styled.nav`
  margin-top: 0;
  padding: 0;
`;
