import React, { useState } from "react";
import Headerplus from "./components/Headerplus";
import Down from "./components/Down";
import { auth } from "./firebase";
import "./login.css";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

function Forgot() {
  const [reset, setReset] = useState("");

  const resetP = (e) => {
    e.preventDefault();
    auth
      .sendPasswordResetEmail(reset)
      .then((auth) => {
          // history.push("/");
        window.alert("Your password reset email has been sent");
      })
      .catch((error) => alert(error.message));
  };

  return (
    <div>
      <Headerplus />

     
{/* <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
  Launch demo modal
</button> */}



      <div className="forget__container form-group">
        <h5>Enter your email</h5>
        <input
          class="form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          type="text"
          value={reset}
          onChange={(e) => setReset(e.target.value)}
        ></input>
        <button class="btn btn-dark" onClick={resetP} type="submit">
          Submit
        </button>
      </div>
     
     
  
      <Down />
    </div>
  );
}

export default Forgot;
